import React, { useState } from 'react';

const SELECTION_IMAGES = Array.from({ length: 10 }, (_, i) => ({
  src: `/assets/player-frames/track${i + 1}.png`,
  index: i + 1
}));

const CONFIRMATION_IMAGES = [
  ...Array.from({ length: 10 }, (_, i) => ({
    src: `/assets/player-frames/confirmation${i + 1}_player1.png`,
    player: 'Player 1',
    index: i + 1
  })),
  ...Array.from({ length: 10 }, (_, i) => ({
    src: `/assets/player-frames/confirmation${i + 1}_player2.png`,
    player: 'Player 2',
    index: i + 1
  }))
];

const PlayerSelectionFlow = ({ onComplete }) => {
  const [phase, setPhase] = useState('selection'); // 'selection' | 'confirmation'
  const [currentSelectionIdx, setCurrentSelectionIdx] = useState(0);
  const [selections, setSelections] = useState([]); // [{p1: id, p2: id}]
  const [confirmed, setConfirmed] = useState(false);

  // Selection phase handlers
  const handleSelection = (p1Id, p2Id) => {
    const newSelections = [...selections];
    newSelections[currentSelectionIdx] = { p1: p1Id, p2: p2Id };
    setSelections(newSelections);
  };

  const handleNext = () => {
    if (currentSelectionIdx < SELECTION_IMAGES.length - 1) {
      setCurrentSelectionIdx(currentSelectionIdx + 1);
    } else {
      setPhase('confirmation');
    }
  };

  // Confirmation phase
  const handleConfirm = () => {
    setConfirmed(true);
    if (onComplete) onComplete(selections);
  };

  // Selection phase UI
  if (phase === 'selection') {
    const img = SELECTION_IMAGES[currentSelectionIdx];
    const current = selections[currentSelectionIdx] || { p1: '', p2: '' };
    return (
      <div className="w-full max-w-2xl mx-auto p-6">
        <div className="mb-6 bg-blue-50 rounded-xl p-4 shadow">
          <h2 className="text-3xl font-extrabold text-blue-900 mb-2 text-center drop-shadow">Player Selection ({currentSelectionIdx + 1}/10)</h2>
          <p className="text-blue-700 text-center text-lg">Enter the Temp IDs for each player as shown in the image below.</p>
        </div>
        <div className="flex flex-col items-center bg-white/80 rounded-xl shadow-lg p-6">
          <img 
            src={img.src} 
            alt={`Selection ${img.index}`} 
            className="rounded-xl border-4 border-blue-200 shadow-lg mb-6 max-h-96 w-full object-contain bg-white"
            style={{ maxWidth: 480 }}
          />
          <div className="flex flex-col md:flex-row gap-8 w-full justify-center mb-4">
            <div className="flex flex-col items-center">
              <label className="font-semibold text-blue-800 mb-2 text-lg">Player 1 Temp ID</label>
              <input
                type="text"
                className="border-2 border-blue-300 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 rounded-lg px-4 py-3 text-center w-28 text-xl font-bold text-gray-900 bg-white transition-all duration-150"
                value={current.p1}
                onChange={e => handleSelection(e.target.value, current.p2)}
                placeholder="e.g. 0"
                autoFocus
              />
            </div>
            <div className="flex flex-col items-center">
              <label className="font-semibold text-blue-800 mb-2 text-lg">Player 2 Temp ID</label>
              <input
                type="text"
                className="border-2 border-blue-300 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 rounded-lg px-4 py-3 text-center w-28 text-xl font-bold text-gray-900 bg-white transition-all duration-150"
                value={current.p2}
                onChange={e => handleSelection(current.p1, e.target.value)}
                placeholder="e.g. 3"
              />
            </div>
          </div>
          <button
            className="mt-6 px-8 py-3 bg-blue-600 text-white rounded-xl font-bold text-lg shadow-md hover:bg-blue-700 transition-all duration-150 disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={!current.p1 || !current.p2}
            onClick={handleNext}
          >
            {currentSelectionIdx < SELECTION_IMAGES.length - 1 ? 'Next' : 'Review Selections'}
          </button>
        </div>
      </div>
    );
  }

  // Confirmation phase UI
  if (phase === 'confirmation') {
    return (
      <div className="w-full max-w-4xl mx-auto p-6">
        <div className="mb-6 bg-green-50 rounded-xl p-4 shadow">
          <h2 className="text-3xl font-extrabold text-green-900 mb-2 text-center drop-shadow">Confirm Your Player Selections</h2>
          <p className="text-green-700 text-center text-lg">Review your selections for each player below. If correct, confirm to continue.</p>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Player 1 Confirmations */}
          <div>
            <h3 className="text-xl font-semibold text-blue-700 mb-2 text-center">Player 1</h3>
            <div className="grid grid-cols-2 gap-4">
              {CONFIRMATION_IMAGES.filter(img => img.player === 'Player 1').map((img, idx) => (
                <div key={img.src} className="relative rounded-xl overflow-hidden border-2 border-blue-200 shadow-lg bg-white">
                  <img src={img.src} alt={`Confirm P1 ${img.index}`} className="w-full h-auto object-contain" />
                  <div className="absolute top-2 left-2 bg-blue-600 text-white text-xs px-3 py-1 rounded shadow">
                    Selected ID: <span className="font-bold text-lg">{selections[idx]?.p1 || '?'}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
          {/* Player 2 Confirmations */}
          <div>
            <h3 className="text-xl font-semibold text-purple-700 mb-2 text-center">Player 2</h3>
            <div className="grid grid-cols-2 gap-4">
              {CONFIRMATION_IMAGES.filter(img => img.player === 'Player 2').map((img, idx) => (
                <div key={img.src} className="relative rounded-xl overflow-hidden border-2 border-purple-200 shadow-lg bg-white">
                  <img src={img.src} alt={`Confirm P2 ${img.index}`} className="w-full h-auto object-contain" />
                  <div className="absolute top-2 left-2 bg-purple-600 text-white text-xs px-3 py-1 rounded shadow">
                    Selected ID: <span className="font-bold text-lg">{selections[idx]?.p2 || '?'}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="mt-10 text-center">
          <button
            className="px-10 py-4 bg-green-600 text-white rounded-2xl font-extrabold text-xl shadow-lg hover:bg-green-700 transition-all duration-150 disabled:opacity-50 disabled:cursor-not-allowed"
            onClick={handleConfirm}
            disabled={confirmed}
          >
            {confirmed ? 'Selections Confirmed!' : 'Confirm and Continue'}
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default PlayerSelectionFlow;
