import React from 'react';

const Sidebar = ({ currentNav, setCurrentNav }) => {
  const navItems = [
    { id: 'Dashboard', label: 'Dashboard' },
    { id: 'Upload Video', label: 'Upload Video' },
    { id: 'Overall Stats', label: 'Match Statistics' },
    { id: 'Recordings', label: 'Recordings' },
    { id: 'Game_01', label: 'Ball Tracking' },
    { id: 'Opponents', label: 'Players' },
    { id: 'Game_02', label: 'Bounce Analysis' },
    { id: 'Payment plans', label: 'Subscription' },
    { id: 'Settings', label: 'Settings' },
  ];

  return (
    <div className="h-screen fixed left-0 top-0 w-64 bg-gray-900 shadow-lg flex flex-col">
      <div className="p-4 border-b border-gray-800">
        <div className="flex items-center space-x-2">
          <div className="h-8 w-8 bg-blue-500 rounded-full"></div>
          <span className="text-xl font-bold text-white">CourtCheck</span>
        </div>
        <div className="mt-2 text-xs text-gray-400">
          Tennis Analysis for UC Davis
        </div>
      </div>

      <nav className="py-6 flex-1 overflow-y-auto">
        <ul className="space-y-1 px-3">
          {navItems.map((item) => (
            <li key={item.id}>
              <button
                className={`flex items-center w-full text-left px-4 py-3 rounded-lg transition-colors ${
                  currentNav === item.id
                    ? 'bg-blue-700 text-white'
                    : 'text-gray-400 hover:bg-gray-800 hover:text-gray-200'
                }`}
                onClick={() => setCurrentNav && setCurrentNav(item.id)}
              >
                <span>{item.label}</span>
                {item.id === 'Upload Video' && (
                  <span className="ml-auto bg-green-600 text-xs px-2 py-1 rounded-full text-white">
                    New
                  </span>
                )}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      <div className="p-4 border-t border-gray-800">
        <div className="flex items-center">
          <div className="w-10 h-10 rounded-full bg-gray-700"></div>
          <div className="ml-3">
            <p className="text-sm font-medium text-white">Coach Williams</p>
            <p className="text-xs text-gray-400">UC Davis Tennis</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
