import React, { useState } from 'react';

const SELECTION_IMAGES = Array.from({ length: 10 }, (_, i) => ({
  src: `/assets/player-frames/track${i + 1}.png`,
  index: i + 1
}));

const CONFIRMATION_IMAGES = [
  ...Array.from({ length: 10 }, (_, i) => ({
    src: `/assets/player-frames/confirmation${i + 1}_player1.png`,
    player: 'Player 1',
    index: i + 1
  })),
  ...Array.from({ length: 10 }, (_, i) => ({
    src: `/assets/player-frames/confirmation${i + 1}_player2.png`,
    player: 'Player 2',
    index: i + 1
  }))
];

const PlayerSelectionFlow = ({ onComplete }) => {
  const [phase, setPhase] = useState('selection'); // 'selection' | 'confirmation'
  const [currentSelectionIdx, setCurrentSelectionIdx] = useState(0);
  const [selections, setSelections] = useState([]); // [{p1: id, p2: id}]
  const [confirmed, setConfirmed] = useState(false);

  // Selection phase handlers
  const handleSelection = (p1Id, p2Id) => {
    const newSelections = [...selections];
    newSelections[currentSelectionIdx] = { p1: p1Id, p2: p2Id };
    setSelections(newSelections);
  };

  const handleNext = () => {
    if (currentSelectionIdx < SELECTION_IMAGES.length - 1) {
      setCurrentSelectionIdx(currentSelectionIdx + 1);
    } else {
      setPhase('confirmation');
    }
  };

  // Confirmation phase
  const handleConfirm = () => {
    setConfirmed(true);
    if (onComplete) onComplete(selections);
  };

  // Selection phase UI
  if (phase === 'selection') {
    const img = SELECTION_IMAGES[currentSelectionIdx];
    const current = selections[currentSelectionIdx] || { p1: '', p2: '' };
    return (
      <div className="w-full max-w-2xl mx-auto p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">Player Selection ({currentSelectionIdx + 1}/10)</h2>
        <div className="flex flex-col items-center">
          <img src={img.src} alt={`Selection ${img.index}`} className="rounded-lg border mb-4 max-h-96" />
          <div className="flex flex-col md:flex-row gap-4 w-full justify-center">
            <div className="flex flex-col items-center">
              <label className="font-semibold text-gray-700 mb-1">Player 1 Temp ID</label>
              <input
                type="text"
                className="border rounded px-3 py-2 text-center w-24"
                value={current.p1}
                onChange={e => handleSelection(e.target.value, current.p2)}
                placeholder="e.g. 0"
              />
            </div>
            <div className="flex flex-col items-center">
              <label className="font-semibold text-gray-700 mb-1">Player 2 Temp ID</label>
              <input
                type="text"
                className="border rounded px-3 py-2 text-center w-24"
                value={current.p2}
                onChange={e => handleSelection(current.p1, e.target.value)}
                placeholder="e.g. 3"
              />
            </div>
          </div>
          <button
            className="mt-6 px-6 py-2 bg-blue-600 text-white rounded font-semibold disabled:opacity-50"
            disabled={!current.p1 || !current.p2}
            onClick={handleNext}
          >
            {currentSelectionIdx < SELECTION_IMAGES.length - 1 ? 'Next' : 'Review Selections'}
          </button>
        </div>
      </div>
    );
  }

  // Confirmation phase UI
  if (phase === 'confirmation') {
    return (
      <div className="w-full max-w-4xl mx-auto p-6">
        <h2 className="text-2xl font-bold text-gray-800 mb-4 text-center">Confirm Your Player Selections</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Player 1 Confirmations */}
          <div>
            <h3 className="text-xl font-semibold text-gray-700 mb-2 text-center">Player 1</h3>
            <div className="grid grid-cols-2 gap-4">
              {CONFIRMATION_IMAGES.filter(img => img.player === 'Player 1').map((img, idx) => (
                <div key={img.src} className="relative rounded-lg overflow-hidden border">
                  <img src={img.src} alt={`Confirm P1 ${img.index}`} className="w-full h-auto" />
                  <div className="absolute top-2 left-2 bg-blue-600 text-white text-xs px-2 py-1 rounded">
                    Selected ID: {selections[idx]?.p1 || '?'}
                  </div>
                </div>
              ))}
            </div>
          </div>
          {/* Player 2 Confirmations */}
          <div>
            <h3 className="text-xl font-semibold text-gray-700 mb-2 text-center">Player 2</h3>
            <div className="grid grid-cols-2 gap-4">
              {CONFIRMATION_IMAGES.filter(img => img.player === 'Player 2').map((img, idx) => (
                <div key={img.src} className="relative rounded-lg overflow-hidden border">
                  <img src={img.src} alt={`Confirm P2 ${img.index}`} className="w-full h-auto" />
                  <div className="absolute top-2 left-2 bg-blue-600 text-white text-xs px-2 py-1 rounded">
                    Selected ID: {selections[idx]?.p2 || '?'}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
        <div className="mt-8 text-center">
          <button
            className="px-8 py-3 bg-green-600 text-white rounded-lg font-semibold text-lg hover:bg-green-700"
            onClick={handleConfirm}
            disabled={confirmed}
          >
            {confirmed ? 'Selections Confirmed!' : 'Confirm and Continue'}
          </button>
        </div>
      </div>
    );
  }

  return null;
};

export default PlayerSelectionFlow;
